pragma solidity 0.4.24;

import "openzeppelin-solidity/contracts/math/SafeMath.sol";
import "openzeppelin-solidity/contracts/ownership/Ownable.sol";
import "openzeppelin-solidity/contracts/token/ERC20/PausableToken.sol";

contract AerumToken is Ownable, PausableToken {

    using SafeMath for uint256;

    event Stake(address indexed staker, address indexed delegate, uint256 amount);

    event Unstake(address indexed staker, address indexed delegate, uint256 amount);

    string public name = "Aerum";
    string public symbol = "XRM";
    uint8 public decimals = 18;
    uint256 public initialSupply = 1000 * 1000 * 1000;

    mapping (address => uint256) timeLock;
    mapping (address => uint256) stakes;
    mapping (address => address) delegates;

    mapping (address => uint256) delegateStakes;
    mapping (address => uint256) stakeLocks;

    constructor() public {
        totalSupply_ = initialSupply * (10 ** uint256(decimals));
        balances[owner] = totalSupply_;
    }

    function stake(address delegate, uint256 amount) external {
        require(balances[msg.sender] >= amount);
        require(stakes[msg.sender] == 0 || delegates[msg.sender] == delegate);

        delegates[msg.sender] = delegate;

        balances[msg.sender] = balances[msg.sender].sub(amount);
        stakes[msg.sender] = stakes[msg.sender].add(amount);
        delegateStakes[delegate] = delegateStakes[delegate].add(amount);
        emit Stake(msg.sender, delegate, amount);
    }

    function unstake(uint256 amount) external {
        address delegate = delegates[msg.sender];
        require(delegate != address(0));
        require(stakes[msg.sender] >= amount);
        require(delegateStakes[delegate].sub(amount) >= stakeLocks[delegate]);

        delegateStakes[delegate] = delegateStakes[delegate].sub(amount);
        stakes[msg.sender] = stakes[msg.sender].sub(amount);
        balances[msg.sender] = balances[msg.sender].add(amount);
        emit Unstake(msg.sender, delegate, amount);
    }

    function stakeInfo() view external returns (address delegate, uint256 amount) {
        return (delegates[msg.sender], stakes[msg.sender]);
    }

    function lockStake(uint256 amount) external {
        require(delegateStakes[msg.sender] >= amount);
        stakeLocks[msg.sender] = amount;
    }

    function lock(address account, uint256 time) onlyOwner external {
        timeLock[account] = time;
    }
}
