pragma solidity ^0.4.17;

import "./ERC20.sol";

contract AtomicSwapEtherToERC20 {

    struct Swap {
        uint256 value;
        address ethTrader;
        uint256 erc20Value;
        address erc20Trader;
        address erc20ContractAddress;
        uint256 openedOn;
    }

    enum States {
        INVALID,
        OPEN,
        CLOSED,
        EXPIRED
    }

    mapping(bytes32 => Swap) private swaps;
    mapping(bytes32 => States) private swapStates;
    mapping(address => bytes32[]) private accountSwaps;

    event Open(bytes32 indexed _swapID, address indexed _ethTrader, address indexed _erc20Trader, uint256 _openedOn);
    event Expire(bytes32 indexed _swapID);
    event Close(bytes32 indexed _swapID);

    modifier onlyInvalidSwaps(bytes32 _swapID) {
        require(swapStates[_swapID] == States.INVALID);
        _;
    }

    modifier onlyOpenSwaps(bytes32 _swapID) {
        require(swapStates[_swapID] == States.OPEN);
        _;
    }

    function open(bytes32 _swapID, uint256 _erc20Value, address _erc20Trader, address _erc20ContractAddress) external onlyInvalidSwaps(_swapID) payable {
        // Store the details of the swap.
        Swap memory swap = Swap({
            value: msg.value,
            ethTrader: msg.sender,
            erc20Value: _erc20Value,
            erc20Trader: _erc20Trader,
            erc20ContractAddress: _erc20ContractAddress,
            openedOn: now
        });
        swaps[_swapID] = swap;
        swapStates[_swapID] = States.OPEN;

        // Update account swaps. Make sure there is no duplicates.
        accountSwaps[msg.sender].push(_swapID);
        if (_erc20Trader != msg.sender) {
            accountSwaps[_erc20Trader].push(_swapID);
        }

        emit Open(_swapID, msg.sender, _erc20Trader, now);
    }

    function close(bytes32 _swapID) external onlyOpenSwaps(_swapID) {
        // Close the swap.
        Swap memory swap = swaps[_swapID];
        swapStates[_swapID] = States.CLOSED;

        // Transfer the ERC20 funds from the ERC20 trader to the ETH trader.
        ERC20 erc20Contract = ERC20(swap.erc20ContractAddress);
        require(swap.erc20Value <= erc20Contract.allowance(swap.erc20Trader, address(this)));
        require(erc20Contract.transferFrom(swap.erc20Trader, swap.ethTrader, swap.erc20Value));

        // Transfer the ETH funds from this contract to the ERC20 trader.
        swap.erc20Trader.transfer(swap.value);

        emit Close(_swapID);
    }

    function expire(bytes32 _swapID) external onlyOpenSwaps(_swapID) {
        // Expire the swap.
        Swap memory swap = swaps[_swapID];
        swapStates[_swapID] = States.EXPIRED;

        // Transfer the ETH value from this contract back to the ETH trader.
        swap.ethTrader.transfer(swap.value);
        emit Expire(_swapID);
    }

    function check(bytes32 _swapID) external view returns (uint256 ethValue, address ethTrader, uint256 erc20Value, address erc20Trader, address erc20ContractAddress, uint256 openedOn, States state) {
        state = swapStates[_swapID];
        Swap memory swap = swaps[_swapID];
        return (swap.value, swap.ethTrader, swap.erc20Value, swap.erc20Trader, swap.erc20ContractAddress, swap.openedOn, state);
    }

    function getAccountSwaps(address account) external view returns (bytes32[]) {
        return accountSwaps[account];
    }
}
