pragma solidity ^0.4.24;

contract CounterAtomicSwapEther {

    struct Swap {
        address openTrader;
        address withdrawTrader;
        uint256 value;
        bytes secretKey;
        uint256 timelock;
        uint256 openedOn;
    }

    enum States {
        INVALID,
        OPEN,
        CLOSED,
        EXPIRED
    }

    mapping(bytes32 => Swap) private swaps;
    mapping(bytes32 => States) private swapStates;
    mapping(address => bytes32[]) private accountSwaps;

    event Open(bytes32 indexed _hash, address indexed _openTrader, uint256 _openedOn);
    event Expire(bytes32 indexed _hash);
    event Close(bytes32 indexed _hash, address indexed _withdrawTrader, bytes _secretKey);

    modifier onlyInvalidSwaps(bytes32 _hash) {
        require(swapStates[_hash] == States.INVALID);
        _;
    }

    modifier onlyOpenSwaps(bytes32 _hash) {
        require(swapStates[_hash] == States.OPEN);
        _;
    }

    modifier onlyClosedSwaps(bytes32 _hash) {
        require(swapStates[_hash] == States.CLOSED);
        _;
    }

    modifier onlyExpirableSwaps(bytes32 _hash) {
        require(now >= swaps[_hash].timelock);
        _;
    }

    modifier onlyWithSecretKey(bytes32 _hash, bytes _secretKey) {
        require(_hash == keccak256(_secretKey));
        _;
    }

    function open(bytes32 _hash, uint256 _timelock) external onlyInvalidSwaps(_hash) payable {
        // Store the details of the swap.
        Swap memory swap = Swap({
            openTrader : msg.sender,
            withdrawTrader : address(0x0),
            value : msg.value,
            secretKey : new bytes(0),
            timelock : _timelock,
            openedOn: now
        });
        swaps[_hash] = swap;
        swapStates[_hash] = States.OPEN;

        // Update open account swaps
        accountSwaps[msg.sender].push(_hash);

        // Trigger open event.
        emit Open(_hash, msg.sender, now);
    }

    function close(bytes32 _hash, bytes _secretKey) external onlyOpenSwaps(_hash) onlyWithSecretKey(_hash, _secretKey) {
        // Close the swap.
        Swap memory swap = swaps[_hash];
        swaps[_hash].secretKey = _secretKey;
        swaps[_hash].withdrawTrader = msg.sender;
        swapStates[_hash] = States.CLOSED;

        // We know withdraw trader only here so store it in case it's different than opening one
        if (swap.withdrawTrader != msg.sender) {
            accountSwaps[msg.sender].push(_hash);
        }

        // Transfer the ETH funds from this contract to the withdrawing trader.
        swap.withdrawTrader.transfer(swap.value);

        // Trigger close event.
        emit Close(_hash, msg.sender, _secretKey);
    }

    function expire(bytes32 _hash) external onlyOpenSwaps(_hash) onlyExpirableSwaps(_hash) {
        // Expire the swap.
        Swap memory swap = swaps[_hash];
        swapStates[_hash] = States.EXPIRED;

        // Transfer the ETH value from this contract back to the ETH trader.
        swap.openTrader.transfer(swap.value);

        // Trigger expire event.
        emit Expire(_hash);
    }

    function check(bytes32 _hash) external view returns (address openTrader, address withdrawTrader, uint256 value, uint256 timelock, uint256 openedOn, States state) {
        state = swapStates[_hash];
        Swap memory swap = swaps[_hash];
        return (swap.openTrader, swap.withdrawTrader, swap.value, swap.timelock, swap.openedOn, state);
    }

    function checkSecretKey(bytes32 _hash) external view onlyClosedSwaps(_hash) returns (bytes secretKey) {
        Swap memory swap = swaps[_hash];
        return swap.secretKey;
    }

    function getAccountSwaps(address account) external view returns (bytes32[]) {
        return accountSwaps[account];
    }
}
