pragma solidity ^0.4.24;

import "openzeppelin-solidity/contracts/ownership/Ownable.sol";
import "openzeppelin-solidity/contracts/math/SafeMath.sol";
import "openzeppelin-solidity/contracts/token/ERC20/ERC20.sol";
import "./Delegate.sol";

/**
 * @title Ethereum-based governance contract
 */
contract Governance is Ownable {

    using SafeMath for uint256;

    event NewDelegate(address indexed delegate, address indexed owner);

    /** AER ERC20 token **/
    ERC20 public token;

    /** List of registered delegates **/
    address[] public delegates;

    /** Mapping of (delegate address => keep alive timestamp) **/
    mapping (address => uint256) public keepAliveTimestamps;

    /** Mapping of blacklisted delegates **/
    mapping (address => bool) public blacklist;

    /** Minimum delegate balance necessary to participate in staking **/
    uint256 public minBalance;
  
    /** Maximum duration between keep alive message and current time to consider delegate active **/
    uint256 public keepAliveDuration;

    /** Check if the sender is a valid delegate **/
    modifier onlyDelegate {
        require(keepAliveTimestamps[msg.sender] > 0);
        _;
    }

    constructor(ERC20 _token) public {
        token = _token;
        minBalance = 100;
        keepAliveDuration = 60 * 60 * 24; // 24h
    }

    /**
    * @dev Set up minimum delegate balance necessary to participate in staking
    */
    function setMinBalance(uint256 balance) external onlyOwner {
        minBalance = balance;
    }

    /**
    * @dev Set up duration between keep alive message and current time to consider delegate active
    */
    function setKeepAliveDuration(uint256 duration) external onlyOwner {
        keepAliveDuration = duration;
    }

    /**
    * @dev Change delegate status in the blacklist
    */
    function updateBlacklist(address delegate, bool blocked) external onlyOwner {
        blacklist[delegate] = blocked;
    }

    /**
    * @dev Create new delegate contract and transfer ownership to a caller
    */
    function createDelegate(address aerum) external returns (address) {
        Delegate delegate = new Delegate(token, this, aerum);
        address owner = msg.sender;
        // transfer ownership to a caller
        delegate.transferOwnership(owner);
        address addr = address(delegate);
        // set initial keep alive timestamp to mark it as a valid delegate
        keepAliveTimestamps[addr] = 1;
        emit NewDelegate(addr, owner);
        return addr;
    }

    /**
    * @dev Register specified delegate after successful voting
    */
    function register(address delegate) external onlyOwner {
        delegates.push(delegate);
    }

    /**
    * @dev Returns number of delegates
    */
    function getDelegateCount() public view returns(uint) {
        return delegates.length;
    }

    /**
    * @dev Update keep alive timestamp for a caller delegate
    */
    function keepAlive() external onlyDelegate {
        keepAliveTimestamps[msg.sender] = block.timestamp;
    }

    /**
    * @dev list of composers for the specified block number
    */
    function getComposers(uint256 blockNum) external view returns(address[]) {
        address[] memory candidates = getValidDelegates();
        if (candidates.length <= 10) {
            return candidates;
        }
        address[] memory composers = new address[](10);
        uint256 first = blockNum.div(1000) % candidates.length;
        uint256 last = first + 10;
        uint256 fromStart = 0;
        if (last > candidates.length) {
            fromStart = last - candidates.length;
            last = candidates.length;
        }
        for (uint256 i = first; i < last; i++) {
            composers[i - first] = candidates[i];
        }
        for (uint256 j = 0; j < fromStart; j++) {
            composers[10 - j - 1] = candidates[j];
        }
        return composers;
    }

    /**
    * @dev list of valid delegates which might be composers
    */
    function getValidDelegates() internal view returns(address[]) {
        address[] memory array = new address[](delegates.length);
        uint16 length = 0;
        for (uint16 i = 0; i < delegates.length; i++) {
            if (isValid(delegates[i])) {
                array[length] = delegates[i];
                length++;
            }
        }
        address[] memory result = new address[](length);
        for (uint16 j = 0; j < length; j++) {
            result[j] = array[j];
        }
        return array;
    }

    /**
    * @dev whether specified delegate can be a composer
    */
    function isValid(address delegate) internal view returns(bool) {
        // Delegate has not been blacklisted
        return !blacklist[delegate] &&
            // Delegate has enough minimal stake to participate in consensus
            token.balanceOf(delegate) >= minBalance &&
            // Delegate has produced a keep-alive message in last 24h
            keepAliveTimestamps[delegate].add(keepAliveDuration) >= block.timestamp;
    }

}
