pragma solidity ^0.4.17;

import "./ERC20.sol";

contract AtomicSwapERC20ToEther {

    struct Swap {
        uint256 ethValue;
        address ethTrader;
        uint256 erc20Value;
        address erc20Trader;
        address erc20ContractAddress;
    }

    enum States {
      INVALID,
      OPEN,
      CLOSED,
      EXPIRED
    }

    mapping (bytes32 => Swap) private swaps;
    mapping (bytes32 => States) private swapStates;

    event Open(bytes32 _swapID, address _closeTrader);
    event Expire(bytes32 _swapID);
    event Close(bytes32 _swapID);

    modifier onlyInvalidSwaps(bytes32 _swapID) {
        require (swapStates[_swapID] == States.INVALID);
        _;
    }

    modifier onlyOpenSwaps(bytes32 _swapID) {
        require (swapStates[_swapID] == States.OPEN);
        _;
    }

    function open(bytes32 _swapID, uint256 _erc20Value, address _erc20ContractAddress, uint256 _ethValue, address _ethTrader) public onlyInvalidSwaps(_swapID) {
        // Transfer value from the ERC20 trader to this contract.
        ERC20 openERC20Contract = ERC20(_erc20ContractAddress);
        require(_erc20Value <= openERC20Contract.allowance(msg.sender, address(this)));
        require(openERC20Contract.transferFrom(msg.sender, address(this), _erc20Value));
        
        // Store the details of the swap.
        Swap memory swap = Swap({
            ethValue: _ethValue,
            ethTrader: _ethTrader,
            erc20Value: _erc20Value,
            erc20Trader: msg.sender,
            erc20ContractAddress: _erc20ContractAddress
        });
        swaps[_swapID] = swap;
        swapStates[_swapID] = States.OPEN;

        emit Open(_swapID, _ethTrader);
    }

    function close(bytes32 _swapID) public onlyOpenSwaps(_swapID) payable {
        // Close the swap.
        Swap memory swap = swaps[_swapID];
        swapStates[_swapID] = States.CLOSED;

        // Transfer the ETH funds from ETH trader to ERC20 trader via contract
        require(msg.value == swap.ethValue);
        swap.erc20Trader.transfer(msg.value);

        // Transfer the ERC20 funds from this contract to the ETH trader.
        ERC20 erc20Contract = ERC20(swap.erc20ContractAddress);
        require(erc20Contract.transfer(swap.ethTrader, swap.erc20Value));
        
        emit Close(_swapID);
    }

    function expire(bytes32 _swapID) public onlyOpenSwaps(_swapID) {
        // Expire the swap.
        Swap memory swap = swaps[_swapID];
        swapStates[_swapID] = States.EXPIRED;

        // Transfer ERC20 value from this contract back to the opening ERC20 trader.
        ERC20 openERC20Contract = ERC20(swap.erc20ContractAddress);
        require(openERC20Contract.transfer(swap.erc20Trader, swap.erc20Value));

        emit Expire(_swapID);
    }

    function check(bytes32 _swapID) public view returns (uint256 erc20Value, address erc20Trader, address erc20ContractAddress, uint256 ethValue, address ethTrader, States state) {
        Swap memory swap = swaps[_swapID];
        return  (swap.erc20Value, swap.erc20Trader, swap.erc20ContractAddress, swap.ethValue, swap.ethTrader, swapStates[_swapID]);
    }
}
